spec.template.spec.containers.[0].env.pattern:
{{- range .Values.<<NAME>>.<<INDEX>>.<<KEY>> }}
      - name: {{ .name }}
        {{- if hasKey . "value" }}
        value: {{ .value | quote }}
        {{- else }}
        valueFrom:
          secretKeyRef:
            name: {{ .valueFrom.secretKeyRef.name }}
            key: {{ .valueFrom.secretKeyRef.key }}
        {{- end }}
      {{- end }} 
{{- end }}

spec.template.spec.containers.[0].resour.pattern:
{{- if .Values.<<NAME>>.<<INDEX>>.<<KEY>> }}
{{- if .Values.<<NAME>>.<<INDEX>>.<<KEY>>.limits }}
limits:
  {{- if .Values.<<NAME>>.<<INDEX>>.<<KEY>>.limits.cpu }}
  cpu: {{ .Values.<<NAME>>.<<INDEX>>.<<KEY>>.limits.cpu }}
  {{- end }}
  {{- if .Values.<<NAME>>.<<INDEX>>.<<KEY>>.limits.memory }}
  memory: {{ .Values.<<NAME>>.<<INDEX>>.<<KEY>>.limits.memory }}
  {{- end }}
{{- end }}
{{- if .Values.<<NAME>>.<<INDEX>>.<<KEY>>.requests }}
requests:
  {{- if .Values.<<NAME>>.<<INDEX>>.<<KEY>>.requests.cpu }}
  cpu: {{ .Values.<<NAME>>.<<INDEX>>.<<KEY>>.requests.cpu }}
  {{- end }}
  {{- if .Values.<<NAME>>.<<INDEX>>.<<KEY>>.requests.memory }}
  memory: {{ .Values.<<NAME>>.<<INDEX>>.<<KEY>>.requests.memory }}
  {{- end }}
{{- end }}
{{- end }}

spec.template.spec.securityContext.pattern:
  {{- if .Values.<<NAME>>.<<INDEX>>.<<KEY>> }}
{{- if .Values.<<NAME>>.<<INDEX>>.<<KEY>>.runAsUser }}
runAsUser: {{ .Values.<<NAME>>.<<INDEX>>.<<KEY>>.runAsUser }}
{{- end }}
{{- if .Values.<<NAME>>.<<INDEX>>.<<KEY>>.runAsGroup }}
runAsGroup: {{ .Values.<<NAME>>.<<INDEX>>.<<KEY>>.runAsGroup }}
{{- end }}
{{- if .Values.<<NAME>>.<<INDEX>>.<<KEY>>.fsGroup }}
fsGroup: {{ .Values.<<NAME>>.<<INDEX>>.<<KEY>>.fsGroup }}
{{- end }}
{{- end }}
